import { SET_ALERT, REMOVE_ALERT } from "../types";

// eslint-disable-next-line import/no-anonymous-default-export
export default (state, action) => {
  switch (action.type) {
    case SET_ALERT:
      return [...state, action.payload];
    case CHECK_BLUR:
      return {
        isTouched: true,
        value: state.value,
      };
    case RESET:
      return {
        isTouched: false,
        value: "",
      };

    default:
      return state;
  }
};
