import { SET_ALERT, REMOVE_ALERT } from "../types";

// eslint-disable-next-line import/no-anonymous-default-export
export default (state, action) => {
  switch (action.type) {
    case SET_ALERT:
      return [...state, action.payload];

    default:
      return state;
  }
};
