  ADD_LIST,
  DELETE_LIST,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_LIST,
  FILTER_LISTS,
  CLEAR_FILTER,
} from "../types";

const ListState = (props) => {
  const initialState = {
    lists: [
      {
        id: 1,
        msg: "yoga",
      },
      {
        id: 2,
        msg: "study",
      },
      {
        id: 3,
        msg: "eat",
      },
    ],
  };

  const [state, dispatch] = useReducer(listReducer, initialState);

  // Add List
